net.rptools.maptool.model.MacroButtonPro.command:
[h: Weapons = getWeapons()] [h: WeaponNames = json.toList(json.getAll(Weapons, "Name"))] [h: abort(input( "WeaponIndex|" + WeaponNames + "|Weapon|LIST|", "Target|" + targetList() +"|Target|RADIO|VALUE=STRING" ))] [h: Weapon = json.get(Weapons, WeaponIndex)] [h: Bonus = json.get(Weapon, "Bonus")] [h: AttackMod = getPropertyWithDefault("Mod Attack", 0)] [h: Ability = if(json.get(Weapon, "Type") == "Ranged", "Dexterity", "Strength")] [h: AbilityMod = getAbilityMod(Ability)] [h: HalfLevel = halfLevel()]
[h: Roll = 1d20] [h: Attack = Roll + AbilityMod + Bonus + AttackMod + HalfLevel] [h: AttackRoll = "1d20 (" + Roll + ") + Modifier (" + (Attack - Roll) + ") = " + Attack + " vs. AC"] [h: IsHit = hitCheck(Attack, Target, "AC")] [h: IsCritical = if(Roll == 20 && IsHit, 1, 0)] [h: Description = if (IsCritical, "scores a critical hit on", if (IsHit, "hits", "misses"))] [h: Pronoun = listGet(pronouns(), 1)]
[gt, st, t(Description): AttackRoll] {Target} with {Pronoun} {json.get(Weapon, "Name")}

[r, if(getInitiativeToken() == currentToken()), CODE: {
    Ending turn
    [h: nextInitiative()]
};{
    [h: assert(0, "Cannot end turn, because it is not your turn")]
}]

