net.rptools.maptool.model.MacroButtonPro.command:
[h: Type = getArg(macro.args, 0, "all")]
[h: IsMultipleTargets = getArg(macro.args, 1, 0)]

[h: Targets = targetList(Type)]

[h, if(IsMultipleTargets == 1), CODE: {
    [h: CheckList = "[]"]
    [h: Format = "Target%d|0|%s|CHECK"]
    [h: NumTargets = listCount(Targets)]
    [h, for(i, 0, NumTargets), CODE: {
        [h: Target = listGet(Targets, i)]
        [h: Line = strformat(Format, i, Target)]
        [h: CheckList = json.append(CheckList, Line)]
    }]
    [h: CheckList = json.toList(CheckList, "##")]
    [h: abort(input(CheckList))]

    [h: Result = ""]
    [h, for(i, 0, NumTargets), CODE: {
        [h: IsSelected = eval("Target" + i)]
        [h, if(IsSelected == 1): Result = listAppend(Result, listGet(Targets, i))]
    }]
};{
    [h: CheckList = "Result|" + Targets +"|Target|RADIO|VALUE=STRING"]
    [h: abort(input(CheckList))]
}]
[h: macro.return = Result]

[h: Token = currentToken()]
[h, if(argCount() == 1): Token = arg(0)]
[h: Gender = getProperty("Gender", Token)]
[h, switch(Gender), code: 
    case "Male": {[macro.return = "he, his, his"]};
    case "Female": {[macro.return = "she, her, hers"]};
    default: {[macro.return = "it, its, its"]}
]

[h: Args = if(json.isEmpty(arg(0)), "[]", arg(0))]
[h: Position = arg(1)]
[h: Default = arg(2)]
[h, if(json.length(Args) > Position), CODE: {
    [h: macro.return = json.get(Args, Position)]
};{
    [h: macro.return = Default]
}]
